import React,{useState} from 'react'
import './Expenses.css'
import ExpenseItem from '../Expenses/ExpenseItem'
import Card from '../UI/Card'
import ExpensesFilter from './ExpenseFilter'

const Expenses = ({expenses}) => {

  const [filteredYear, setFilteredYear] = useState('2020');
  const getYear =(selectedYear)=>{
    setFilteredYear(selectedYear);
  }

  const selectedYearExpenses = expenses.filter((expense) => {
    return expense.date.getFullYear().toString() === filteredYear;
  });

  let expenseContent = <p>No expenses Found</p>

  if(expenses.length>0){
    expenseContent=selectedYearExpenses.map((item)=>(
        <ExpenseItem
        key={item.id}
        title={item.title}
        price={item.price}
        date={item.date}
      />
      ))
  }

  return (
    <div>
    <Card className="expenses">
      <ExpensesFilter selected={filteredYear} getYear={getYear}/>
      {/* {selectedYearExpenses.map((item)=>(
        <ExpenseItem
        key={item.id}
        title={item.title}
        price={item.price}
        date={item.date}
      />
      ))} */}
      {expenseContent}
     </Card>
    </div>
  );
}

export default Expenses
